mapper.select:
	select * from zsqy_v2.interfaceconfig

	1=1

	and  explain like concat("%",#{explain},"%") 


	and interfaceType =#{interfaceType}



          limit #{startIndex},#{pageSize}

mapper.update:
update zsqy_v2.interfaceconfig

interfaceType=#{interfaceConfig.interfaceType},
url=#{interfaceConfig.url}, username=#{interfaceConfig.username}, 
`password`=#{interfaceConfig.password},appid=#{interfaceConfig.appid},
`key` =#{interfaceConfig.key}, `explain`=#{interfaceConfig.explain}

where id=#{interfaceConfig.id}
